//This command is used as an include to import the function in other projects
//Call 
// HKS.Action.Run("Includes");
//in the Application Node Code to define the functions at activation time.
//You can copy this object or it's group and paste it in other 
//applications you are writing script for


function TapOrLongPress(delay,key)
{
    //This function returns T or LP based on passed delay and key or mouse button code
    
    //delay   the time in ms to hold long press
    //key     the "key" or "mouse button" ...

    try //Try to do this...
    {
        //Get the time at press
        var d1 = new Date();
        //while the key or button is still down
        while(""+GetKeyState(key , "P") == "1")
        {
            Sleep(10);
        }
        //get the time at release
        var d2 = new Date();
        //Choose between the 2 macros based on delay value
        var dt = d2.getTime()-d1.getTime();
        if(dt < delay)
        {
            OutputDebug("Tap " + key);
            return "T";
        }
        else
        {
            OutputDebug("Long Press " + key)
            return "LP";
        }
    }
    catch(e) //catch error
    {
        
    }
    return ""; //this should never happen
}

function WaitForRelease(key)
{
    //This function waits for the key to be released

    //key     the "key" or "mouse button" ...

    try //Try to do this...
    {
        //while the key or button is still down
        OutputDebug("Hold " + key)
        while(""+GetKeyState(key , "P") == "1")
        {
            Sleep(10);
        }
        OutputDebug("Released " + key)
    }
    catch(e) //catch error
    {
        
    }
}

function CountTaps(period,maxTaps,key)
{
    //This function returns the number to times the key was tapped during a time period
    
    //period  the time period (ms) to allow for another key press to happen
    //key     the "key" or "mouse button" ...
    //maxTaps  the maximum number of taps allow, used for for early exit ot of time period

    try //Try to do this...
    {
        //Get the time at press
        var d1 = new Date();
        var tp = 0;
        var d2;
        var tapsCt = 0;
        //While the time period has not expired and maxTap not reached
        while(tp<period && tapsCt<maxTaps)
        {
            if(""+GetKeyState(key , "P") == "1")
            {
                tapsCt++;
                WaitForRelease(key);
            }
            //get the time period
            var d2 = new Date();
            //Choose between the 2 macros based on delay value
            tp = d2.getTime()-d1.getTime();
            Sleep(10);
        }
        OutputDebug("Taps Count for " + key + ": " + tapsCt);
        return tapsCt;
    }
    catch(e) //catch error
    {
        
    }
    return 0; //
}

//calling function as it's being declared
(function() {
    try //Try to do this...
    {
        //Hotkey("1", "Off"); //See NonBlocking Example
        if(CountTaps(200,2,"1")>1)
        {
            //Detected Double Tap
            Hotkey("1", "Off"); 
            Send("Double Tap 1");
            Hotkey("1", "On");
        }
        else
        {
            //Pass on the key pressed
            Hotkey("1", "Off"); 
            Send("1");
            Hotkey("1", "On");
        }
        //Hotkey("1", "On");
    }
    catch(e) //catch error
    {
    }
})();

//When you assign a hot key, in the this case, 3, 
//the application will not receive the key as the system blocked it.
//You have to pass it on youself. However you need to tell the system 
//to stop pressessing that hot key otherwise it may cause a endless 
//loop or a recursion stack error.
//Hotkey("3", "Off") will help.


(function() {
    OutputDebug("TapOrLongPress example");

    //Turn off the hot key
     
    ///Hotkey("3", "Off"); //Placed here will allow code boxes to repeat type the character being held down while teh wait for release happens in the TapOrLongPress function
    if( TapOrLongPress(200,"3") === "T")
    {
        //Tap, pass it on
        Hotkey("3", "Off"); //*Placed here will prevent the Key from being re-detected and re-processed by the hot key system
        Send("3"); //sen 3 un release
        Hotkey("3", "On");
    }
    else //"LP" or "" returned
    {
        //Long Press, sen 300 instead
        Hotkey("3", "Off"); //Same as * above
        Send("300{Enter}");
        Hotkey("3", "On");
    }
    //1. it is interesting to see how both position
    //Hotkey("3", "Off");    

})();

(function() {
    OutputDebug("TapOrLongPress example");

    //Tap or long press Function Declared in Application 
    if( TapOrLongPress(200,"XButton2") === "T")
    {
        //Tap
        Send("Tap XButton2{Enter}");
    }
    else //"LP" or "" returned
    {
        //Long Press
        Send("Long Press XButton2{Enter}");
    }


})();

(function() {
    OutputDebug("WaitForRelease example");
    
    //Send a down on ANOTHER key. 
    Send("{W Down}");
    Send("{Space Down}");
    //Wait for release
    WaitForRelease("XButton1");
    //Send a Release for that OTHER key. 
    Send("{Space Up}");    
    Send("{W Up}{Enter}");


})();

